import"./input-gZM7-Ci6.js";document.addEventListener("DOMContentLoaded",function(){const n=document.getElementById("name-input"),o=document.getElementById("pass-input"),a=document.getElementById("done-btn");document.getElementById("close-btn").addEventListener("click",t=>{t.preventDefault(),window.location.href="/index.html"});const c=async t=>{t.preventDefault();try{const e=await fetch("http://localhost:8001/api/login",{method:"POST",headers:{"Content-Type":"application/json"},body:JSON.stringify({id:n.value,pass:o.value})});console.log(n.value,o.value);const s=await e.json();if(e.ok)localStorage.setItem("token",s.token),window.location.href="/index.html";else throw new Error(s.message)}catch(e){console.error(e)}};a.addEventListener("click",c)});
